import React, { useState } from "react";

const Example = () => {
  const [color, setColor] = useState("black");

  const handleClick = () => {
    setColor("red");
  };

  return (
    <div>
      <p style={{ color: color }}>This is a text</p>
      <button onClick={handleClick}>Change Color</button>
    </div>
  );
};

export default Example;
