import React, { useRef } from "react";

const FocusInput = () => {
  const inputRef = useRef(null);

  const handleFocus = () => {
    inputRef.current.focus(); // فوکوس بر روی ورودی
  };

  return (
    <div>
      <input ref={inputRef} type="text" />
      <button onClick={handleFocus}>فوکوس بر روی ورودی</button>
    </div>
  );
};

export default FocusInput;
